//------------------------------------------------------------------------------
//  Function    : SNR_NVP6124B_Cust_CheckSnrTVSrcType
//  Description :
//------------------------------------------------------------------------------
void SNR_NVP6124B_Cust_CheckSnrTVSrcType(MMP_UBYTE ubSnrSel, MMP_SNR_TVDEC_SRC_TYPE *TVSrcType)
{    
    MMP_USHORT usData;

    gsSensorFunction->MMPF_Sensor_GetReg(ubSnrSel, 0xFF, &usData);
 
    if (usData != 0x00) {
        gsSensorFunction->MMPF_Sensor_SetReg(ubSnrSel, 0xFF, 0x00);
    }

    gsSensorFunction->MMPF_Sensor_GetReg(ubSnrSel, 0xD0, &usData);
    
    if (usData & 0x04) // HD TVBS signal input
    {
        *TVSrcType =  MMP_SNR_TVDEC_SRC_HD;
    }
    else if (usData & 0x40) // FHD TVBS signal input
    {
        *TVSrcType =  MMP_SNR_TVDEC_SRC_FHD;
    }
    else  // No TVBS signal
    {
        *TVSrcType =  MMP_SNR_TVDEC_SRC_NO_READY;
    }

    //printc("SNR_NVP6124B_Cust_CheckSnrTVSrcType: %d\r\n", *TVSrcType);
}

